    const correctanswer = (question % 2 == 0) ? 'yes' : 'no';
    const answer = readlineSync.question('Your answer:');
    if (answer == correctanswer) {
      console.log('Correct!');
    } else {
      console.log(`Is wrong answer! Let's try again!${name}`);
    }
  }
};

evenGame(name);

